#!/bin/bash

java -cp target/colors-1.0-SNAPSHOT-jar-with-dependencies.jar weka.experiment.Experiment -T data/emo_lausanne_CN_DE_UK_GR_emotionVector.csv -P weka.experiment.AveragingResultProducer -D "InstancesResultListener -O res/res-color-predict.arff" -F Fold -X 10 -W weka.experiment.CrossValidationResultProducer -- -X 10 -O splitEvalutorOut.zip -W weka.experiment.ClassifierSplitEvaluator -- -W weka.classifiers.meta.FilteredClassifier -I 0 -C 3 -- -F "weka.filters.unsupervised.attribute.Remove -R 2" -W weka.classifiers.meta.GridSearch -- -E ACC -y-property kernel.gamma -y-min -3.0 -y-max 3.0 -y-step 1.0 -y-base 10.0 -y-expression 'pow(BASE,I)' -x-property C -x-min -3.0 -x-max 3.0 -x-step 1.0 -x-base 10.0 -x-expression 'pow(BASE,I)' -sample-size 100.0 -traversal ROW-WISE -log-file /home/wicker -num-slots 1 -S 1 -W weka.classifiers.functions.SMO -- -C 1.0 -L 0.001 -P 1.0E-12 -N 0 -V -1 -W 1 -K "weka.classifiers.functions.supportVector.RBFKernel -G 0.01 -C 250007" -calibrator "weka.classifiers.functions.Logistic -R 1.0E-8 -M -1 -num-decimal-places 4"  
